(5, 'Añadir CPT a consulta', '', 'add_action( \'pre_get_posts\', \'add_my_post_types_to_query\' );\r\n\r\nfunction add_my_post_types_to_query( $query ) {\r\n	if ( (is_single() || is_home() || is_category() ) && $query->is_main_query() )\r\n		$query->set( \'post_type\', array( \'post\', \'precios_post_type\' ) );\r\n\r\n	return $query;\r\n}\r\n', '', 'global', 10, 1);
